import React, { useState } from 'react';
import './App.css';

const App = () => {
    const [title, setTitle] = useState('');
    const [message, setMessage] = useState('');
    const [color, setColor] = useState('rgb(245, 96, 96)'); // default color is red
    const [notes, setNotes] = useState([]);
    const [error, setError] = useState('');

    const handleTitleChange = (e) => {
        setTitle(e.target.value);
        setError('');
    };

    const handleMessageChange = (e) => {
        setMessage(e.target.value);
        setError('');
    };

    const handleColorChange = (e) => {
        setColor(e.target.value);
    };

    const createNote = () => {
        if (!title || !message) {
            setError('Missing Values');
            return;
        }

        const note = {
            title,
            message,
            color,
        };

        setNotes([...notes, note]);
        setTitle('');
        setMessage('');
        setColor('');
    };

    const deleteNote = (index) => {
      const newNotes = [...notes];
      newNotes.splice(index, 1);
      setNotes(newNotes);
    };

  const editNote = (index) => {
    const note = notes[index];
    setTitle(note.title);
    setMessage(note.message);
    setColor(note.color);
    deleteNote(index);
  };



    return (
        <div id="wrapper">
            <div id="editor">
                <h2 id="editor-title">Bongani's Note Taking App</h2>
                <label>Title:</label>
                <input type="text" value={title} onChange={handleTitleChange} placeholder="Title" />
                <label>Description:</label>
                <textarea rows="10" cols="42" value={message} onChange={handleMessageChange} placeholder="Message"></textarea>
                <label>Select Colour:</label>
                <select onChange={handleColorChange}>
                  <option value="rgb(245, 96, 96)">Red</option>
                  <option value="pink">Pink</option>
                  <option value="rgb(255, 255, 119)">Yellow</option>
                  <option value="rgb(157, 209, 246)">Blue</option>
                </select>
                <div id="button">
                    <button id="btn" onClick={createNote}>Create Note</button>
                    {error && <div id="error">{error}</div>}
                </div>
            </div>

            <div id="notes-section">
                <h2>Notes</h2>
                <ul id="notes">
                  {notes.map((note, index) => (
                    <li key={index} style={{ backgroundColor: note.color }}>
                        <h2>{note.title}</h2>
                        <p>{note.message}</p>
                    </li>
                  ))}
                </ul>
            </div>
        </div>
    );
};

export default App;
